export class DetailsComponent {
  constructor(route, translate) {
    this.route = route;
    this.translate = translate;
    this.title = '';
    this.formattedText = '';
    this.items = [{
      title: 'gasgo-vazisubani',
      images: [],
      text: 'DETAILS.gasgoVazisubani.description'
    }, {
      title: 'tbilisi-marriott',
      images: ['../../assets/Projects/meriot 1.jpg', '../../assets/Projects/meriot 2.jpg', '../../assets/Projects/meriot 3.jpg', '../../assets/Projects/meriot 4.jpg'],
      text: 'DETAILS.tbilisiMariott.description'
    }, {
      title: 'urban-x',
      images: ['../../assets/Projects/Urban x picture 1 .jpg', '../../assets/Projects/Urban x picture 2.jpg', '../../assets/Projects/Urban x picture 3.jpg', '../../assets/Projects/Urban x picture 4.jpg', '../../assets/Projects/Urban x picture 5.jpg'],
      text: 'DETAILS.urbanX.description'
    }, {
      title: 'oda-vazisubani',
      images: ['../../assets/Projects/oda 1.jpg', '../../assets/Projects/oda 2.jpg', '../../assets/Projects/oda 3.jpg', '../../assets/Projects/oda 4.jpg'],
      text: 'DETAILS.odaVazisubani.description'
    }];
    this.route.paramMap.subscribe(params => {
      this.title = params.get('title') || '';
      this.item = this.items.find(i => i.title === this.title);
      if (this.item) {
        this.translate.get(this.item.text).subscribe(res => {
          this.formattedText = `<p>${res.split('\n\n').join('</p><p>')}</p>`;
        });
      }
    });
  }
}
