          i0.ɵɵelementEnd();
          i0.ɵɵtext(123, " \u10EC\u10E7\u10D0\u10DA\u10DB\u10D0 \u10E8\u10D4\u10D8\u10EB\u10DA\u10D4\u10D1\u10D0 \u10D2\u10D0\u10DB\u10DD\u10D8\u10EC\u10D5\u10D8\u10DD\u10E1 \u10D1\u10D4\u10E2\u10DD\u10DC\u10D8\u10E1, \u10EE\u10D8\u10E1 \u10D3\u10D0 \u10DB\u10D4\u10E2\u10D0\u10DA\u10D8\u10E1 \u10D9\u10DD\u10DC\u10E1\u10E2\u10E0\u10E3\u10E5\u10EA\u10D8\u10D4\u10D1\u10D8\u10E1 \u10D3\u10D0\u10D6\u10D8\u10D0\u10DC\u10D4\u10D1\u10D0, \u10DB\u10D0\u10D7\u10D8 \u10D2\u10D0\u10DB\u10EB\u10DA\u10D4\u10DD\u10D1\u10D8\u10E1 \u10E8\u10D4\u10DB\u10EA\u10D8\u10E0\u10D4\u10D1\u10D0 \u10D3\u10D0 \u10E1\u10D0\u10D1\u10DD\u10DA\u10DD\u10DD\u10D3 \u10E8\u10D4\u10DC\u10DD\u10D1\u10D8\u10E1 \u10E1\u10E2\u10E0\u10E3\u10E5\u10E2\u10E3\u10E0\u10E3\u10DA\u10D8 \u10DB\u10D7\u10DA\u10D8\u10D0\u10DC\u10DD\u10D1\u10D8\u10E1 \u10D3\u10D0\u10E0\u10E6\u10D5\u10D4\u10D5\u10D0.");
          i0.ɵɵelementEnd();
          i0.ɵɵelementStart(124, "li")(125, "strong");
          i0.ɵɵtext(126, "\u10EF\u10D0\u10DC\u10DB\u10E0\u10D7\u10D4\u10DA\u10DD\u10D1\u10D8\u10E1 \u10D3\u10D0\u10EA\u10D5\u10D0:");
          i0.ɵɵelementEnd();
          i0.ɵɵtext(127, " \u10DC\u10D4\u10E1\u10E2\u10D8 \u10EE\u10D4\u10DA\u10E1 \u10E3\u10EC\u10E7\u10DD\u10D1\u10E1 \u10DD\u10D1\u10D8\u10E1\u10D0 \u10D3\u10D0 \u10E1\u10DD\u10D9\u10DD\u10E1 \u10D2\u10D0\u10DC\u10D5\u10D8\u10D7\u10D0\u10E0\u10D4\u10D1\u10D0\u10E1, \u10E0\u10DD\u10DB\u10DA\u10D4\u10D1\u10D8\u10EA \u10E1\u10D0\u10D6\u10D8\u10D0\u10DC\u10DD\u10D0 \u10D0\u10D3\u10D0\u10DB\u10D8\u10D0\u10DC\u10D8\u10E1 \u10EF\u10D0\u10DC\u10DB\u10E0\u10D7\u10D4\u10DA\u10DD\u10D1\u10D8\u10E1\u10D7\u10D5\u10D8\u10E1 \u10D3\u10D0 \u10D8\u10EC\u10D5\u10D4\u10D5\u10E1 \u10D0\u10DA\u10D4\u10E0\u10D2\u10D8\u10E3\u10DA \u10E0\u10D4\u10D0\u10E5\u10EA\u10D8\u10D4\u10D1\u10E1\u10D0 \u10D3\u10D0 \u10E0\u10D4\u10E1\u10DE\u10D8\u10E0\u10D0\u10E2\u10DD\u10E0\u10E3\u10DA \u10DE\u10E0\u10DD\u10D1\u10DA\u10D4\u10DB\u10D4\u10D1\u10E1.");
          i0.ɵɵelementEnd();
          i0.ɵɵelementStart(128, "li")(129, "strong");
          i0.ɵɵtext(130, "\u10E4\u10D8\u10DC\u10D0\u10DC\u10E1\u10E3\u10E0\u10D8 \u10D3\u10D0\u10DC\u10D0\u10D6\u10DD\u10D2\u10D8:");
          i0.ɵɵelementEnd();
