          i0.ɵɵelementEnd();
          i0.ɵɵelementStart(139, "li")(140, "strong");
          i0.ɵɵtext(141, "\u10EE\u10D8\u10E1\u10E2\u10D8 \u10F0\u10D8\u10D3\u10E0\u10DD\u10D8\u10D6\u10DD\u10DA\u10D0\u10EA\u10D8\u10D0:");
          i0.ɵɵelementEnd();
          i0.ɵɵtext(142, " \u10DB\u10DD\u10D8\u10EA\u10D0\u10D5\u10E1 \u10EA\u10D4\u10DB\u10D4\u10DC\u10E2\u10D8\u10E1 \u10D3\u10D0\u10E4\u10E3\u10EB\u10DC\u10D4\u10D1\u10E3\u10DA \u10DC\u10D0\u10E0\u10D4\u10D5\u10D4\u10D1\u10E1, \u10E0\u10DD\u10DB\u10DA\u10D4\u10D1\u10D8\u10EA \u10DE\u10D8\u10E0\u10D3\u10D0\u10DE\u10D8\u10E0 \u10D6\u10D4\u10D3\u10D0\u10DE\u10D8\u10E0\u10D6\u10D4 \u10D2\u10D0\u10DB\u10DD\u10D8\u10E7\u10D4\u10DC\u10D4\u10D1\u10D0 \u10D3\u10D0 \u10E5\u10DB\u10DC\u10D8\u10E1 \u10EE\u10D8\u10E1\u10E2, \u10EC\u10E7\u10D0\u10DA\u10D2\u10D0\u10E3\u10DB\u10E2\u10D0\u10E0 \u10E4\u10D4\u10DC\u10D0\u10E1.");
